import 'dart:convert';import 'package:carros_app/domain/carro.dart';import 'package:http/http.dart' as http;class CarroService {  static Future<List<Carro>> getCarros(String tipo) async {    var url = "http://livrowebservices.com.br/rest/carros/tipo/$tipo";    final response = await http.get(url);    final mapCarros = json.decode(response.body).cast<Map<String, dynamic>>();    final carros =        mapCarros.map<Carro>((json) => Carro.fromJson(json)).toList();    return carros;  }  static Future<String> getLoremIpsum() async {    final url = "https://loripsum.net/api";    final response = await http.get(url);    return response.body.replaceAll("<p>", "").replaceAll("</p>", "");  }}